mapper.select:
SELECT c.id , c.chat_date , c.content ,
u.profile_image , u.id AS FromId, u.nick_name
FROM e_chat c , e_user u WHERE ((u.id=c.from_user AND from_user=#{fromUser,jdbcType=VARCHAR} AND to_user=#{toUser,jdbcType=VARCHAR}) OR (u.id=c.from_user AND from_user=#{toUser,jdbcType=VARCHAR} AND to_user=#{fromUser,jdbcType=VARCHAR})) ORDER BY chat_date ASC;

SELECT profile_image , u.id AS userId, nick_name, c.id AS chatId,
chat_date, c.content, is_read AS isRead
FROM (SELECT * FROM e_chat WHERE 1=1 ORDER BY chat_date DESC ) c , e_user u WHERE u.id=c.from_user AND to_user=#{toUser,jdbcType=VARCHAR} GROUP BY c.from_user;

select count(*) from e_chat

	1=1

		and from_user = #{fromUser,jdbcType=VARCHAR}


		and to_user = #{toUser,jdbcType=VARCHAR}


		and is_delete = #{isDelete,jdbcType=VARCHAR}

mapper.update:
		update e_chat
		set is_read = 1
        WHERE from_user=#{toUser,jdbcType=VARCHAR} AND to_user=#{fromUser,jdbcType=VARCHAR};
